CREATE TABLE recharge_records (id SERIAL PRIMARY KEY, user_id INTEGER NOT NULL REFERENCES users(id) ON DELETE CASCADE, amount DECIMAL(10,2) NOT NULL, points_received INTEGER NOT NULL, payment_method VARCHAR(50), payment_status VARCHAR(20) DEFAULT 'pending', transaction_id VARCHAR(255), created_at TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP, updated_at TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP);